import React, { useEffect, useState } from "react";
import firebase from "./firebase"

function ShopItems(props) {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false); 
  const ref = firebase.firestore().collection("Items");
  let sum = 0;

  function getItems() {

    setLoading(true);
    ref.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setItems(items);
      setLoading(false);
    });
  }

  const addItem= (price) =>{
    sum+=price;
    console.log(sum);
  }

  useEffect(() => {
    getItems();
  }, []);

  if(loading) {
    return <h1>Loading...</h1>
  }

  return (
    <div className="App">
     {items.map((item) => (
        <div key={item.id}>
          <h2>{item.name}</h2>
          <p>{item.price}</p>
          <button onClick={addItem(item.price)}>
            Add To cart
          </button>
        </div>
     ))}
    </div>
  );


}

export default ShopItems;
