import React, { useState } from "react";


function Cart(props){
    const {cartItems, onAdd} = props;
    let i = 0;
    return (     
        <div>
            <h2>Basket Items</h2>
           <div> {cartItems.length === 0 && <div>Cart is Empty</div>}</div>
            {cartItems.map((item) => (       
                <div key={i++} >
                    <div>{item.name}</div>
                    <div>
                        <button onClick={() => onAdd(item)}>+</button>
                    </div>
                    <div>
                        {item.qty}
                    </div>
                    <div>
                        {item.price}
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Cart;
