import React, { useState } from "react";


function Cart(props){
    const {cartItems, onAdd} = props;

    return (     
        <div>
            <h2>Basket Items</h2>
           <div> {cartItems.length === 0 && <div>Cart is Empty</div>}</div>
            {cartItems.map((item) => (
                <div key={item.id}>
                    <div>{item.name}</div>
                    <div>
                        {item.qty} x ${item.price}
                    </div>
                    <button onClick= {() => onAdd(item)}></button>
                </div>
            ))}
        </div>
    )
}

export default Cart;
