import React, { useState } from "react";


function Cart(props){
    const {cartItems, onAdd} = props;
    let i = 0;
    return (     
        <div>
            <h2>Basket Items</h2>
           <div> {cartItems.length === 0 && <div>Cart is Empty</div>}</div>
            {cartItems.map((item) => (       
                <div key={i++} className="row">
                    <div className="col-2">{item.name}</div>
                </div>
            ))}
        </div>
    )
}

export default Cart;
