import React, { useState } from "react";
import './App.css';

function App() {
  const [schools, setSchools] = useState([]);
  const [loading, setLoading] = useState(false);

  const ref = firebase.firestore().collection("schools");
  console.log(ref);

  if(loading) {
    return <h1>Loading...</h1>
  }

  return (
    <div className="App">
     {schools.map((school) => (
        <div key={school.id}>
          <h2>{schools.title}</h2>
          <p>{school.desc}</p>
        </div>
     ))}
    </div>
  );
}

export default App;
