import React, { useState } from "react";

const PAGE_ORDER = 'order';
const PAGE_CHECKOUT = 'checkout';

function Checkout(props) {
    const {cartItems} = props;
    let i = 0;

    const handleSubmit=(e)=>{
        e.preventDefault();
    }

    return (
        <div>
            <form>
                <label htmlFor="email">Email
                    <input type="email" id="email" required></input>
                </label>
                <br></br>
                <label htmlFor="adress">Adress
                    <input type="text" id="adress" required></input>
                </label>
                <br></br>
                <label htmlFor="card">Credit Card number
                    <input type="text" id="card" required></input>
                </label>
                <br></br>
                <button onSubmit={handleSubmit}>Checkout</button>
            </form>

            {cartItems.map((item) => (       
                <div key={i++} >
                    <div>{item.name}</div>
                    <div>
                        {item.qty} x ${item.price.toFixed(2)}
                    </div>
                    <div>
                        {item.price} 
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Checkout;
