import React, { useState } from "react";


function Cart(props){
    const {cartItems, onAdd, onRemove} = props;
    let itemsPrice = cartItems.reduce((a, c) => a+c.price*c.qty, 0);
    let useOtherPromo = 0;

    const handleSubmit=(e)=>{
        e.preventDefault();
        const promotion=e.target.promo.value;
        if(promotion==="20%OFF"){
            itemsPrice = itemsPrice * 0.2
            useOtherPromo = 1;
        }
        if(useOtherPromo===0){
            if(promotion==="5%OFF"){
                console.log("this works aswell");
            }
            if(promotion==="20EUROFF"){
                console.log("20 euro less");
            }
        }
    }

    let i = 0;
    return (     
        <div>
            <h2>Basket Items</h2>
           <div> {cartItems.length === 0 && <div>Cart is Empty</div>}</div>
            {cartItems.map((item) => (       
                <div key={i++} >
                    <div>{item.name}</div>
                    <div>
                        <button onClick={() => onAdd(item)}>+</button>
                    </div>
                    <div>
                        <button onClick={() => onRemove(item)}>-</button>
                    </div>
                    <div>
                        {item.qty} x ${item.price.toFixed(2)}
                    </div>
                    <div>
                        {item.price} 
                    </div>
                </div>
            ))}
            {cartItems.length !== 0 && (
                <div> Price
                    <p>${itemsPrice.toFixed(2)}</p>
                </div>
            )}
            <div>
                <form onSubmit={handleSubmit}>
                    <input type="text" name="promo" placeholder="promotion"></input>
                    <button>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default Cart;
