import React, { useState } from "react";

function Checkout(props) {
    const {cartItems} = props;
    let i = 0;
    return (
        <div>
            <form>
                <input type="email" required></input>
                <button>Checkout</button>
            </form>

            {cartItems.map((item) => (       
                <div key={i++} >
                    <div>{item.name}</div>
                    <div>
                        {item.qty} x ${item.price.toFixed(2)}
                    </div>
                    <div>
                        {item.price} 
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Checkout;
