import React, { useEffect, useState } from "react";
import './App.css';
import firebase from "./firebase"

function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);

  const ref = firebase.firestore().collection("Items");


  function getItems() {
    setLoading(true);
    ref.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setItems(items);
      setLoading(false);
    });
  }

  useEffect(() => {
    getItems();
  }, []);

  if(loading) {
    return <h1>Loading...</h1>
  }

  return (
    <div className="App">
     {items.map((item) => (
        <div key={item.id} className={"class"+item.id}>
          <h2>{item.name}</h2>
          <p>{item.price}</p>
        </div>
     ))}
    </div>
  );

  function addValuesToItems() {
    for(let i=1;i<6;i++){
      const value = document.querySelector(`.class${i}`)
      value.addEventListener('click', () => {
        console.log("hello world");
      })
    }
  }

  addValuesToItems();
}

export default App;
